import React, { useState } from 'react';
import './.css'; // Assurez-vous d'avoir un fichier de style pour le calendrier

const Calendrier = () => {
  const [jourDebut, setJourDebut] = useState(15); // Jour de début pour le calendrier
  const [salles, setSalles] = useState(['Salle1', 'Salle2']); // Exemple de listes de salles

  // Fonction pour faire défiler horizontalement vers la gauche
  const scrollGauche = () => {
    setJourDebut(jourDebut - 1);
  };

  // Fonction pour faire défiler horizontalement vers la droite
  const scrollDroite = () => {
    setJourDebut(jourDebut + 1);
  };

  return (
    <div className="calendrier-container">
      <div className="calendrier-header">
        <button onClick={scrollGauche}>{'<'}</button>
        {['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'].map((jour, index) => (
          <div key={index}>{jour}</div>
        ))}
        <button onClick={scrollDroite}>{'>'}</button>
      </div>
      <div className="calendrier-body">
        {salles.map((salle, index) => (
          <div key={index}>
            <div>{salle}</div>
            {Array.from({ length: 7 }, (_, i) => jourDebut + i).map((jour) => (
              <div key={jour}>{jour}</div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendrier;
