import React, { useState } from 'react';
import { Button, Grid } from '@material-ui/core';

const Calendrier = () => {
  const [jourDebut, setJourDebut] = useState(15); // Jour de début pour le calendrier
  const [salles, setSalles] = useState([
    { nom: 'Salle1', contenu: { 15: 'Hello' } },
    { nom: 'Salle2', contenu: { 17: 'Test' } },
  ]); // Informations sur les salles

  // Fonction pour faire défiler horizontalement vers la gauche
  const scrollGauche = () => {
    setJourDebut(jourDebut - 1);
  };

  // Fonction pour faire défiler horizontalement vers la droite
  const scrollDroite = () => {
    setJourDebut(jourDebut + 1);
  };

  return (
    <div>
      <Grid container alignItems="center">
        <Grid item xs={1}>
          <Button onClick={scrollGauche}>{'<'}</Button>
        </Grid>
        {[...Array(7)].map((_, index) => (
          <Grid key={index} item xs={1}>
            <div>
              <div>{new Date(2024, 4, jourDebut + index).toLocaleDateString('en-US', { day: 'numeric' })}</div>
              <div>{new Date(2024, 4, jourDebut + index).toLocaleDateString('en-US', { weekday: 'short' })}</div>
            </div>
          </Grid>
        ))}
        <Grid item xs={1}>
          <Button onClick={scrollDroite}>{'>'}</Button>
        </Grid>
      </Grid>
      <Grid container>
        {salles.map((salle, index) => (
          <Grid key={index} container>
            <Grid item xs={1}>
              {salle.nom}
            </Grid>
            {[...Array(7)].map((_, index) => (
              <Grid key={index} item xs={1}>
                {salle.contenu[jourDebut + index] || ''}
              </Grid>
            ))}
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default Calendrier;
