import React, { useState } from 'react';
import { Button, Grid } from '@material-ui/core';
import './Calendrier.css'; // Importez votre fichier CSS

const Calendrier = () => {
  const [mois, setMois] = useState(1); // Mois du calendrier
  const [annee, setAnnee] = useState(2024); // Année du calendrier

  // Fonction pour faire défiler horizontalement vers la gauche (par mois)
  const scrollGauche = () => {
    if (mois === 1) {
      setMois(12);
      setAnnee(prev => prev - 1);
    } else {
      setMois(prev => prev - 1);
    }
  };

  // Fonction pour faire défiler horizontalement vers la droite (par mois)
  const scrollDroite = () => {
    if (mois === 12) {
      setMois(1);
      setAnnee(prev => prev + 1);
    } else {
      setMois(prev => prev + 1);
    }
  };

  // Générer les jours du mois
  const joursDuMois = () => {
    const nbJours = new Date(annee, mois, 0).getDate();
    return Array.from({ length: nbJours }, (_, index) => index + 1);
  };

  return (
    <div className="calendrier-container">
      <div className="calendrier-header">
        <Button onClick={scrollGauche}>{'<'}</Button>
        <div>{new Date(annee, mois - 1).toLocaleDateString('default', { month: 'long', year: 'numeric' })}</div>
        <Button onClick={scrollDroite}>{'>'}</Button>
      </div>
      <div className="calendrier-body">
        {joursDuMois().map(jour => (
          <div key={jour} className="calendrier-day">{jour}</div>
        ))}
      </div>
    </div>
  );
};

export default Calendrier;
