import React, { useState } from 'react';

export function Calendar({ room }) {
  const handleCellClick = (info) => {
    const title = prompt('Titre de la réunion :');
    const description = prompt('Description de la réunion :');
    // Ici vous pouvez ajouter la logique pour ajouter la réunion au calendrier
    console.log('Nouvelle réunion :', title, description, 'dans la salle', room);
  };

  // Vous pouvez intégrer votre composant FullCalendar ici
  // Exemple simplifié :
  return (
    <div>
      <h2>Calendrier de la salle {room}</h2>
      <p>Cliquez sur une cellule pour ajouter une réunion.</p>
      {/* Ici se trouverait votre composant FullCalendar */}
      <div onClick={handleCellClick} style={{ border: '1px solid black', height: '400px', cursor: 'pointer' }}>
        Calendrier
      </div>
    </div>
  );
}
