import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react'; // Importez le composant FullCalendar
import timeGridPlugin from '@fullcalendar/timegrid'; // Plugin pour afficher la vue quotidienne
import interactionPlugin from '@fullcalendar/interaction'; // Plugin pour les interactions comme le clic
import './App.css'; // Styles CSS

// Composant Room pour afficher les détails d'une salle
const Room = ({ name, capacity }) => (
  <div className="room">
    <div className="room-name">{name}</div>
    <div className="room-capacity">Capacité : {capacity}</div>
  </div>
);

// Composant principal de l'application
function App() {
  const [selectedRoom, setSelectedRoom] = useState('salle1'); // Par défaut, la salle 1 est sélectionnée
  const [showForm, setShowForm] = useState(false); // État pour afficher/cacher le formulaire
  const [selectedDate, setSelectedDate] = useState(null); // État pour stocker la date sélectionnée
  const [events, setEvents] = useState([ // État pour stocker les événements
    // Exemple d'événements pour la salle sélectionnée
    { title: 'Réunion 1', start: '2024-05-01T08:00:00', end: '2024-05-01T10:00:00', roomId: 'salle1' },
    { title: 'Réunion 2', start: '2024-05-01T10:30:00', end: '2024-05-01T12:30:00', roomId: 'salle2' },
    { title: 'Réunion 3', start: '2024-05-01T13:00:00', end: '2024-05-01T15:00:00', roomId: 'salle3' },
    { title: 'Réunion 4', start: '2024-05-01T15:30:00', end: '2024-05-01T17:30:00', roomId: 'salle1' },
    { title: 'Réunion 5', start: '2024-05-01T18:00:00', end: '2024-05-01T20:00:00', roomId: 'salle2' },
  ]);

  // Gestionnaire de changement de salle
  const handleRoomChange = (event) => {
    setSelectedRoom(event.target.value);
  };

  // Gestionnaire de clic sur une cellule du calendrier
  const handleDateClick = (info) => {
    setSelectedDate(info.dateStr); // Stocker la date sélectionnée
    setShowForm(true); // Afficher le formulaire
  };

  // Gestionnaire pour soumettre le formulaire de réunion
  const handleSubmit = (event) => {
    event.preventDefault();
    const title = event.target.title.value;
    const description = event.target.description.value;
    const newEvent = {
      title: title,
      start: selectedDate,
      end: selectedDate,
      roomId: selectedRoom,
    };
    setEvents([...events, newEvent]); // Ajouter la nouvelle réunion à la liste des événements
    setShowForm(false); // Cacher le formulaire après soumission
  };

  return (
    <div className="app-container">
      {/* Sélecteur de salle */}
      <div className="room-selector">
        <label>
          Choisissez une salle :
          <select value={selectedRoom} onChange={handleRoomChange}>
            <option value="salle1">Salle 1</option>
            <option value="salle2">Salle 2</option>
            <option value="salle3">Salle 3</option>
          </select>
        </label>
      </div>

      {/* Affichage du calendrier en fonction de la salle sélectionnée */}
      <div className="calendar-container">
        <FullCalendar
          plugins={[timeGridPlugin, interactionPlugin]}
          initialView="timeGridDay" // Vue initiale avec la grille horaire quotidienne
          height="600px"
          dateClick={handleDateClick} // Gestionnaire pour gérer le clic sur une cellule du calendrier
          events={events.filter((event) => event.roomId === selectedRoom)} // Filtrer les événements pour n'afficher que ceux de la salle sélectionnée
          headerToolbar={{
            left: 'prev,next today',
            center: 'title',
            right: '',
          }}
          slotLabelFormat={{ hour: 'numeric', minute: '2-digit', omitZeroMinute: false }} // Format d'affichage des heures
          slotDuration="00:30:00" // Durée de chaque cellule dans la grille horaire
          slotEventOverlap={false} // Désactiver le chevauchement des événements dans les cellules
          slotLabelInterval="01:00:00" // Intervalles d'affichage des étiquettes de temps
          eventContent={({ event }) => (
            <div>
              <b>{event.title}</b>
              <p>{event.roomId}</p>
            </div>
          )}
          eventClick={(info) => {
            alert(`Détails de la réunion : ${info.event.title}`);
          }}
        />
      </div>

      {/* Affichage des salles */}
      <div className="rooms-container">
        <div className="room">
          <div className="room-name">Room</div>
          <div className="room-capacity">Capacity</div>
        </div>
        <Room name="Salle 1" capacity="10" />
        <Room name="Salle 2" capacity="15" />
        <Room name="Salle 3" capacity="8" />
      </div>

      {/* Formulaire pour ajouter une réunion */}
      {showForm && (
        <div className="meeting-form">
          <h2>Ajouter une réunion</h2>
          <form onSubmit={handleSubmit}>
            <label>
              Titre :
              <input type="text" name="title" />
            </label>
            <label>
              Description :
              <input type="text" name="description" />
            </label>
            <button type="submit">Ajouter</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
