import React from "react";

function RoomSelector({ onSelectRoom }) {
  const rooms = [
    { id: "meeting", title: "Salle de réunion" },
    { id: "conference", title: "Salle de conférence" },
    { id: "event", title: "Espace événementiel" }
  ];

  const handleRoomChange = (e) => {
    onSelectRoom(e.target.value);
  };

  return (
    <div>
      <label>Choisir une salle :</label>
      <select onChange={handleRoomChange}>
        <option value="">Sélectionner une salle</option>
        {rooms.map(room => (
          <option key={room.id} value={room.id}>{room.title}</option>
        ))}
      </select>
    </div>
  );
}

export default RoomSelector;
