import React, { useState } from 'react';

function RoomTypeForm({ onSubmit }) {
  const [roomType, setRoomType] = useState('');

  const handleChange = (event) => {
    setRoomType(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(roomType);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Choisissez le type de salle :
        <select value={roomType} onChange={handleChange}>
          <option value="">Sélectionnez un type</option>
          <option value="salle_de_reunion">Salle de réunion</option>
          <option value="salle_de_conference">Salle de conférence</option>
          <option value="salle_de_formation">Salle de formation</option>
          <option value="autre">Autre</option>
        </select>
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}

export default RoomTypeForm;
