// RoomCalendar.js
import React from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function RoomCalendar({ selectedRoom }) {
  const handleDateClick = (arg) => {
    // Gérer le clic sur une date dans le calendrier
    // Peut-être ouvrir un formulaire pour créer un événement pour cette date et cette salle
  };

  const eventRender = (info) => {
    // Personnaliser le rendu de chaque événement dans le calendrier
    const event = info.event;
    const title = event.title;
    const comment = event.extendedProps.comment;
    const participants = event.extendedProps.participants;

    const startDate = event.start;
    const endDate = event.end;

    const startTime = startDate.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
    const endTime = endDate.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });

    info.el.innerHTML = `
      <div>
        <strong>${title}</strong> - ${comment} <br>
        Début : ${startTime} - Fin : ${endTime} <br>
        ${participants ? "Participants : " + participants : ""}
      </div>
    `;
  };

  return (
    <div>
      <h2>{selectedRoom.title}</h2>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView={"dayGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"90vh"}
        dateClick={handleDateClick} // Gestionnaire de clic sur une date
        eventRender={eventRender} // Personnalisation du rendu des événements
        // Ajoutez les autres props nécessaires pour le calendrier
      />
    </div>
  );
}

export default RoomCalendar;
