import React, { useState, useRef } from 'react';
import { Grid } from '@material-ui/core';
import './Calendrier.css'; // Importez votre fichier CSS

const Calendrier = () => {
  const [jourDebut, setJourDebut] = useState(1); // Jour de début pour le calendrier
  const [salles, setSalles] = useState([
    { nom: 'Salle1', contenu: { 1: 'Hello' } },
    { nom: 'Salle2', contenu: { 3: 'Test' } },
  ]); // Informations sur les salles

  const scrollRef = useRef(null);

  // Fonction pour détecter le défilement horizontal
  const handleScroll = () => {
    const scrollLeft = scrollRef.current.scrollLeft;
    if (scrollLeft < 100) {
      setJourDebut(prev => prev - 7);
      scrollRef.current.scrollLeft += 100;
    } else if (scrollLeft > 300) {
      setJourDebut(prev => prev + 7);
      scrollRef.current.scrollLeft -= 100;
    }
  };

  const dateDebut = new Date(2024, 4, jourDebut);
  const dernierJourDuMois = new Date(2024, 4 + 1, 0).getDate(); // Obtenez le dernier jour du mois

  return (
    <div>
      <div style={{ marginBottom: '10px', overflowX: 'auto' }} ref={scrollRef} onScroll={handleScroll}>
        <div>{dateDebut.toLocaleString('default', { month: 'long' })} {dateDebut.getFullYear()}</div>
        <Grid container>
          {[...Array(14)].map((_, index) => (
            <Grid key={index} item xs={1}>
              <div>{new Date(2024, 4, jourDebut + index).toLocaleDateString('en-US', { weekday: 'short' })}</div>
              <div>{(jourDebut + index) > dernierJourDuMois ? jourDebut + index - dernierJourDuMois : jourDebut + index}</div>
            </Grid>
          ))}
        </Grid>
      </div>
      <Grid container>
        {salles.map((salle, index) => (
          <Grid key={index} container>
            <Grid item xs={1}>
              {salle.nom}
            </Grid>
            {[...Array(14)].map((_, index) => (
              <Grid key={index} item xs={1}>
                {salle.contenu[jourDebut + index] || ''}
              </Grid>
            ))}
          </Grid>
        ))}
      </Grid>
    </div>
  );
};

export default Calendrier;
