import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react'; // Importez le composant FullCalendar
import dayGridPlugin from '@fullcalendar/daygrid'; // Plugin pour afficher la vue mensuelle
import interactionPlugin from '@fullcalendar/interaction'; // Plugin pour les interactions comme le clic
import './App.css'; // Styles CSS

// Composant principal de l'application
function App() {
  const [selectedRoom, setSelectedRoom] = useState('salle1'); // Par défaut, la salle 1 est sélectionnée
  const [showForm, setShowForm] = useState(false); // État pour suivre l'affichage du formulaire

  // Gestionnaire de changement de salle
  const handleRoomChange = (event) => {
    setSelectedRoom(event.target.value);
  };

  // Gestionnaire de clic sur une cellule du calendrier
  const handleDateClick = (info) => {
    setShowForm(true);
  };

  // Gestionnaire de soumission du formulaire
  const handleSubmit = (event) => {
    event.preventDefault();
    const title = event.target.elements.title.value;
    const description = event.target.elements.description.value;
    const start = event.target.elements.start.value;
    const end = event.target.elements.end.value;

    // Créez un nouvel événement à partir des données du formulaire
    const newEvent = {
      title: title,
      start: start,
      end: end,
      roomId: selectedRoom,
      description: description,
    };

    // Ajoutez le nouvel événement à la liste des événements (ou envoyez-le à votre backend, selon votre cas d'utilisation)
    console.log('Nouvel événement :', newEvent);

    // Cachez le formulaire après soumission
    setShowForm(false);
  };

  return (
    <div className="app-container">
      {/* Sélecteur de salle */}
      <div className="room-selector">
        <label>
          Choisissez une salle :
          <select value={selectedRoom} onChange={handleRoomChange}>
            <option value="salle1">Salle 1</option>
            <option value="salle2">Salle 2</option>
            <option value="salle3">Salle 3</option>
          </select>
        </label>
      </div>

      {/* Affichage du calendrier en fonction de la salle sélectionnée */}
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          height="600px"
          dateClick={handleDateClick} // Gestionnaire de clic sur une cellule du calendrier
          events={[
            // Exemple d'événements pour la salle sélectionnée
            { title: 'Réunion 1', start: '2024-05-01T10:00:00', end: '2024-05-01T12:00:00', roomId: 'salle1' },
            { title: 'Réunion 2', start: '2024-05-05T14:00:00', end: '2024-05-05T16:00:00', roomId: 'salle1' },
            { title: 'Réunion 3', start: '2024-05-10T09:00:00', end: '2024-05-10T11:00:00', roomId: 'salle2' },
            { title: 'Réunion 4', start: '2024-05-15T13:00:00', end: '2024-05-15T15:00:00', roomId: 'salle2' },
            { title: 'Réunion 5', start: '2024-05-20T08:00:00', end: '2024-05-20T10:00:00', roomId: 'salle3' },
          ]}
        />

        {/* Formulaire d'ajout de réunion */}
        {showForm && (
          <form className="event-form" onSubmit={handleSubmit}>
            <h3>Ajouter une réunion</h3>
            <label>
              Titre :
              <input type="text" name="title" required />
            </label>
            <label>
              Description :
              <textarea name="description" rows="3"></textarea>
            </label>
            <label>
              Début :
              <input type="datetime-local" name="start" required />
            </label>
            <label>
              Fin :
              <input type="datetime-local" name="end" required />
            </label>
            <button type="submit">Ajouter</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default App;
