import React, { useState } from 'react';
import Calendar from 'react-calendar';
import './Calendrier.css'; // Assurez-vous d'avoir un fichier de style pour le calendrier

const Calendrier = () => {
  const [date, setDate] = useState(new Date());
  const [salles, setSalles] = useState(['Salle1', 'Salle2']); // Exemple de listes de salles

  // Fonction pour afficher les jours suivants
  const afficherJoursSuivants = () => {
    const newDate = new Date(date);
    newDate.setDate(newDate.getDate() + 7); // Ajouter 7 jours
    setDate(newDate);
  };

  // Fonction pour afficher les jours précédents
  const afficherJoursPrecedents = () => {
    const newDate = new Date(date);
    newDate.setDate(newDate.getDate() - 7); // Soustraire 7 jours
    setDate(newDate);
  };

  return (
    <div className="calendrier-container">
      <div className="calendrier-header">
        <button onClick={afficherJoursPrecedents}>{'<'}</button>
        <Calendar value={date} onChange={setDate} />
        <button onClick={afficherJoursSuivants}>{'>'}</button>
      </div>
      <div className="calendrier-body">
        {salles.map((salle, index) => (
          <div key={index}>
            <div>{salle}</div>
            {/* Afficher les jours pour chaque salle ici */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Calendrier;
