import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [eventInfo, setEventInfo] = useState(null);
  const [calendarEvents, setCalendarEvents] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState("All");
  const [selectedReservation, setSelectedReservation] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleDateClick = (arg) => {
    const eventData = {
      start: arg.date,
      end: arg.date,
      allDay: true,
    };
    setEventInfo(eventData);
  };

  const handleEventClick = (arg) => {
    setSelectedReservation(arg.event);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedReservation(null);
    setShowModal(false);
  };

  const handleDeleteReservation = () => {
    setCalendarEvents(calendarEvents.filter(event => event.id !== selectedReservation.id));
    handleCloseModal();
  };

  const handleSubmitReservation = (updatedReservation) => {
    setCalendarEvents(calendarEvents.map(event =>
      event.id === updatedReservation.id ? updatedReservation : event
    ));
    handleCloseModal();
  };

  const generateEventId = () => {
    return Math.random().toString(36).substring(7);
  };

  const eventRender = (info) => {
    const event = info.event;
    const userName = event.extendedProps.userName;
    const comment = event.extendedProps.comment;
    const participants = event.extendedProps.participants;

    const startDate = event.start;
    const endDate = event.end;

    const daysDiff = Math.ceil(
      (endDate.getTime() - startDate.getTime()) / (1000 * 3600 * 24)
    );

    info.el.innerHTML = `
      <div>
        <strong>${userName}</strong> - ${comment} <br>
        for ${daysDiff} ${daysDiff > 1 ? "days" : "day"} <br>
        ${participants ? "Participants: " + participants : ""}
      </div>
    `;

    info.el.addEventListener("click", () => {
      handleEventClick(info);
    });
  };

  const handleRoomSelect = (e) => {
    setSelectedRoom(e.target.value);
  };

  return (
    <div>
      <div>
        <label>Select Room:</label>
        <select value={selectedRoom} onChange={handleRoomSelect}>
          <option value="All">All Rooms</option>
          <option value="meeting">Meeting Room</option>
          <option value="conference">Conference Room</option>
          <option value="event">Event Space</option>
        </select>
      </div>
      <FullCalendar
        plugins={[dayGridPlugin, timeGridPlugin, interactionPlugin]}
        initialView={"dayGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "dayGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"90vh"}
        dateClick={handleDateClick}
        eventDrop={handleEventDrop}
        events={
          selectedRoom === "All"
            ? calendarEvents
            : calendarEvents.filter((event) => event.roomType === selectedRoom)
        }
        editable={true}
        eventRender={eventRender}
      />
      {showModal && (
        <ReservationModal
          reservation={selectedReservation}
          onDelete={handleDeleteReservation}
          onSubmit={handleSubmitReservation}
          onClose={handleCloseModal}
        />
      )}
      {eventInfo && (
        <EventForm eventInfo={eventInfo} onSubmit={handleSubmit} />
      )}
    </div>
  );
}

function ReservationModal({ reservation, onDelete, onSubmit, onClose }) {
  const [formData, setFormData] = useState({
    userName: reservation.extendedProps.userName,
    comment: reservation.extendedProps.comment,
    participants: reservation.extendedProps.participants,
    color: reservation.backgroundColor,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleCancel = () => {
    setFormData({
      userName: reservation.extendedProps.userName,
      comment: reservation.extendedProps.comment,
      participants: reservation.extendedProps.participants,
      color: reservation.backgroundColor,
    });
    onClose();
  };

  const handleSubmit = () => {
    const updatedReservation = {
      ...reservation,
      title: formData.userName,
      extendedProps: {
        ...reservation.extendedProps,
        userName: formData.userName,
        comment: formData.comment,
        participants: formData.participants,
      },
      backgroundColor: formData.color,
    };
    onSubmit(updatedReservation);
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h2>Reservation Details</h2>
        <label>User Name:</label>
        <input
          type="text"
          name="userName"
          value={formData.userName}
          onChange={handleChange}
        />
        <label>Comment:</label>
        <textarea
          name="comment"
          value={formData.comment}
          onChange={handleChange}
        ></textarea>
        <label>Participants:</label>
        <input
          type="text"
          name="participants"
          value={formData.participants}
          onChange={handleChange}
        />
        <label>Color:</label>
        <input
          type="color"
          name="color"
          value={formData.color}
          onChange={handleChange}
        />
        <button onClick={handleSubmit}>Save</button>
        <button onClick={onDelete}>Delete</button>
        <button onClick={handleCancel}>Cancel</button>
      </div>
    </div>
  );
}

function EventForm({ eventInfo, onSubmit }) {
  const [formData, setFormData] = useState({
    userName: "",
    comment: "",
    startDate: eventInfo.start,
    endDate: eventInfo.end,
    participants: "",
    color: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="event-form">
      <h2>New Event</h2>
      <form onSubmit={handleSubmit}>
        <label>User Name:</label>
        <input
          type="text"
          name="userName"
          value={formData.userName}
          onChange={handleChange}
        />
        <label>Comment:</label>
        <textarea
          name="comment"
          value={formData.comment}
          onChange={handleChange}
        ></textarea>
        <label>Start Date:</label>
        <input
          type="date"
          name="startDate"
          value={formData.startDate}
          onChange={handleChange}
        />
        <label>End Date:</label>
        <input
          type="date"
          name="endDate"
          value={formData.endDate}
          onChange={handleChange}
        />
        <label>Participants:</label>
        <input
          type="text"
          name="participants"
          value={formData.participants}
          onChange={handleChange}
        />
        <label>Color:</label>
        <input
          type="color"
          name="color"
          value={formData.color}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Calendar;
