import React, { useState } from "react";
import FullCalendar from "@fullcalendar/react";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";

function Calendar() {
  const [eventInfo, setEventInfo] = useState(null);
  const [calendarEvents, setCalendarEvents] = useState([]);

  const handleDateClick = (arg) => {
    const eventData = {
      start: arg.date,
      end: arg.date,
      allDay: true,
    };
    setEventInfo(eventData);
  };

  const handleEventDrop = (arg) => {
    const updatedEvent = {
      ...arg.event.toPlainObject(),
      start: arg.event.start,
      end: arg.event.end,
    };

    const updatedEvents = calendarEvents.map((event) =>
      event.id === updatedEvent.id ? updatedEvent : event
    );

    setCalendarEvents(updatedEvents);
  };

  const handleSubmit = (formData) => {
    const newEvent = {
      title: formData.name,
      start: eventInfo.start,
      end: eventInfo.end,
      allDay: eventInfo.allDay,
      color: formData.color,
      otherUsers: formData.otherUsers,
      comment: formData.comment,
      id: generateEventId(),
    };

    setCalendarEvents([...calendarEvents, newEvent]);
    setEventInfo(null);
  };

  const generateEventId = () => {
    return Math.random().toString(36).substring(7);
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", height: "90vh" }}>
      <FullCalendar
        plugins={[timeGridPlugin, interactionPlugin]}
        initialView={"timeGridMonth"}
        headerToolbar={{
          start: "today prev,next",
          center: "title",
          end: "timeGridMonth,timeGridWeek,timeGridDay",
        }}
        height={"100%"}
        dateClick={handleDateClick}
        eventDrop={handleEventDrop}
        events={calendarEvents}
        editable={true}
      />
      {eventInfo && <EventForm eventInfo={eventInfo} onSubmit={handleSubmit} />}
    </div>
  );
}

function EventForm({ eventInfo, onSubmit }) {
  const [formData, setFormData] = useState({
    name: "",
    duration: "",
    comment: "",
    color: "",
    otherUsers: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="event-form">
      <h2>New Event</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Calendar;
