// EventForm.js
import React, { useState } from "react";

 function EventForm({ selectedRoom }) {
  const [formData, setFormData] = useState({
    event: "",
    comment: "",
    participants: "",
    color: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Créez un nouvel événement avec les données du formulaire
    const newEvent = {
      title: formData.event,
      roomType: selectedRoom.id,
      comment: formData.comment,
      start: formData.start,
      end: formData.end,
      allDay: true, // Nous n'avons pas de gestionnaire pour les heures actuellement
      participants: formData.participants,
      color: formData.color,
      id: generateEventId(),
    };

    // Ajoutez la logique pour mettre à jour la liste des événements de la salle sélectionnée
    // setRooms([...rooms, newEvent]);

    // Réinitialiser le formulaire
    setFormData({
      event: "",
      comment: "",
      participants: "",
      color: ""
    });
  };

  return (
    <div className="event-form">
      <h2>Nouvel événement</h2>
      <form onSubmit={handleSubmit}>
        <label>Nom de l'événement :</label>
        <input
          type="text"
          name="event"
          value={formData.event}
          onChange={handleChange}
        />
        <label>Commentaire :</label>
        <textarea
          name="comment"
          value={formData.comment}
          onChange={handleChange}
        ></textarea>
        <label>Participants :</label>
        <input
          type="text"
          name="participants"
          value={formData.participants}
          onChange={handleChange}
        />
        <label>Couleur :</label>
        <input
          type="color"
          name="color"
          value={formData.color}
          onChange={handleChange}
        />
        <button type="submit">Soumettre</button>
      </form>
    </div>
  );
}
