import React, { useState } from 'react';
import FullCalendar from '@fullcalendar/react'; 
import dayGridPlugin from '@fullcalendar/daygrid'; 
import interactionPlugin from '@fullcalendar/interaction'; // Plugin pour les interactions comme le clic
import './App.css'; // Styles CSS

// Composant principal de l'application
function App() {
  const [selectedRoom, setSelectedRoom] = useState('salle1'); 
  const [showForm, setShowForm] = useState(false); 
  const [selectedDate, setSelectedDate] = useState(null); 
  const [events, setEvents] = useState([ 
    
    { title: 'Réunion 1', start: '2024-05-01', end: '2024-05-01', roomId: 'salle1' },
    { title: 'Réunion 2', start: '2024-05-05', end: '2024-05-05', roomId: 'salle1' },
    { title: 'Réunion 3', start: '2024-05-10', end: '2024-05-10', roomId: 'salle2' },
    { title: 'Réunion 4', start: '2024-05-15', end: '2024-05-15', roomId: 'salle2' },
    { title: 'Réunion 5', start: '2024-05-20', end: '2024-05-20', roomId: 'salle3' },
  ]);

  // Gestionnaire de changement de salle
  const handleRoomChange = (event) => {
    setSelectedRoom(event.target.value);
  };

  // Gestionnaire de clic sur une cellule du calendrier
  const handleDateClick = (info) => {
    setSelectedDate(info.dateStr); // Stocker la date sélectionnée
    setShowForm(true); // Afficher le formulaire
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const title = event.target.title.value;
    const description = event.target.description.value;
    const newEvent = {
      title: title,
      start: selectedDate,
      end: selectedDate,
      roomId: selectedRoom,
    };
    setEvents([...events, newEvent]); 
    setShowForm(false); 
  };

  return (
    <div className="app-container">
      {/* Sélecteur de salle */}
      <div className="room-selector">
        <label>
          Choisissez une salle :
          <select value={selectedRoom} onChange={handleRoomChange}>
            <option value="salle1">Salle 1</option>
            <option value="salle2">Salle 2</option>
            <option value="salle3">Salle 3</option>
          </select>
        </label>
      </div>

      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          height="600px"
          dateClick={handleDateClick} 
          events={events.filter((event) => event.roomId === selectedRoom)} 
          eventContent={({ event }) => (
            <div>
              <b>{event.title}</b>
              <p>{event.roomId}</p>
            </div>
          )}
          eventClick={(info) => {
            alert(`Détails de la réunion : ${info.event.title}`);
          }}
        />
      </div>

      {showForm && (
        <div className="meeting-form">
          <h2>Ajouter une réunion</h2>
          <form onSubmit={handleSubmit}>
            <label>
              Titre :
              <input type="text" name="title" />
            </label>
            <label>
              Description :
              <input type="text" name="description" />
            </label>
            <button type="submit">Ajouter</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default App;
