import React, { useState } from "react";
import RoomSelector from "./RoomSelector";
import RoomCalendar from "./RoomCalendar";
import EventForm from "./EventForm";

function Calendar() {
  const [selectedRoom, setSelectedRoom] = useState("");

  return (
    <div>
      <RoomSelector onSelectRoom={setSelectedRoom} />
      <RoomSelector rooms={rooms} onSelectRoom={handleSelectRoom} />
      {selectedRoom && <RoomCalendar selectedRoom={selectedRoom} />}
      {selectedRoom && <EventForm selectedRoom={selectedRoom} />}
      <EventForm selectedRoom={selectedRoom} generateEventId={generateEventId} />

    </div>
  );
}

export default Calendar;
