// RoomSelector.js
import React, { useState } from "react";

function RoomSelector({ rooms, onSelectRoom }) {
  const [selectedRoom, setSelectedRoom] = useState("");

  const handleChange = (e) => {
    const room = e.target.value;
    setSelectedRoom(room);
    onSelectRoom(room);
  };

  return (
    <div>
      <label>Choisir une salle :</label>
      <select value={selectedRoom} onChange={handleChange}>
        <option value="">SÃ©lectionner une salle</option>
        {rooms.map(room => (
          <option key={room.id} value={room.id}>{room.title}</option>
        ))}
      </select>
    </div>
  );
}

export default RoomSelector;
